mapper.insert:
    insert into projects (
		prj_domain
		, prj_name
		, prj_date
		, prj_enabled
	) 
    values (
		#{prj_domain}
		, #{prj_name}
		, sysdate
		, 1				
	)

    insert into ProjectMember (
		prj_domain
		, memb_mail
		, pjmb_right
	) 
    values (
		#{prj_domain}
		, #{memb_mail}
		, #{pjmb_right}
	)
