mapper.update:
UPDATE taskReply SET treply_content = #{treply_content}
, treply_ogFile, treply_saveFile
, treply_inputDate = sysdate
WHERE treply_seq = #{treply_seq}

mapper.insert:
	INSERT INTO taskReply
	(
		    treply_seq, task_seq, treply_writer, treply_content
		    
		    	, treply_ogFile, treply_saveFile
			
    		, treply_inputdate
	) VALUES (
			treply_seq.nextval, #{task_seq}, #{treply_writer}, #{treply_content}
			
				, #{treply_ogFile}, #{treply_saveFile}
			
			, sysdate
	)

mapper.delete:
DELETE FROM taskReply
WHERE 
		treply_seq = #{treply_seq}		
	AND treply_writer = #{treply_writer}
